CONTEXT(heading_31, 'Penalty for this subsection imprisonment for 14 years.')
heading_32 = heading_30 + ">>content2"
CONTEXT(heading_32, 'Summary conviction penalty for this subsection imprisonment for 3 years and a fine of $36 000.')
heading_31 = heading_29 + ">>(2) A charge of an offence under subsection (1) "
heading_32 = heading_31 + ">>a"
CONTEXT(heading_32, 'must specify the period during which it is alleged that the acts of family violence occurred and')
heading_33 = heading_31 + ">>b"
CONTEXT(heading_33, 'need not specify the dates, or in any other way particularise the circumstances, of the acts of family violence that are alleged to constitute the offence.')
heading_32 = heading_29 + ">>(3) Subsection (2) applies despite the"
CONTEXT(heading_32, 'Criminal Procedure Act 2004 sections 23 and 85.')
