heading_12 = heading_10 + ">>(2)"
CONTEXT(heading_12, 'Subsection (1) does not apply if Part 2 Division 3 provides for a longer limitation period for commencing the action.')
heading_13 = heading_10 + ">>(3) In subsection (1) "
