CONTEXT(heading_19, 'for person A or person B not to commence an action against person C or')
heading_19 = heading_17 + ">>content2"
CONTEXT(heading_19, '(ii) for person A not to wish to divulge the conduct or events in respect of which an action against person C would be founded.')
heading_11 = heading_6 + ">>34. Defamation"
CONTEXT(heading_11, 'Sections 30, 31, 32 and 33 do not apply to an action relating to the publication of defamatory matter.')
heading_7 = heading_5 + ">>Division 2  Extension for persons with mental disability"
heading_8 = heading_7 + ">>35. Suspension of time while person with mental disability is without guardian"
heading_9 = heading_8 + ">>(1) If "
heading_10 = heading_9 + ">>a"
CONTEXT(heading_10, 'a person is suffering a mental disability at any time after a cause of action accrues to the person and')
