heading_11 = heading_9 + ">>(2)"
CONTEXT(heading_11, 'Subsection (1) does not apply if Part 2 Division 2 or 3 provides for a longer limitation period for commencing the action.')
heading_12 = heading_9 + ">>(3)"
CONTEXT(heading_12, 'Despite subsection (1), an action on the cause of action cannot be commenced if 30 years have elapsed since the cause of action accrued.')
heading_13 = heading_9 + ">>(4) In subsection (1) "
heading_14 = heading_13 + ">>content1"
CONTEXT(heading_14, 'defendant includes a person for whom a defendant is vicariously liable')
