heading_28 = heading_25 + ">>(c) any other relevant matter."
CONTEXT(heading_28, '[Section 41 inserted No. 9 of 2023 s. 5.]')
heading_22 = heading_15 + ">>42. Further provisions relating to criminal liability of officers of bodies corporate"
heading_23 = heading_22 + ">>(1)"
CONTEXT(heading_23, 'This Chapter does not affect the liability of a body corporate for any offence.')
heading_24 = heading_22 + ">>(2)"
CONTEXT(heading_24, 'This Chapter does not affect the liability of an officer, or any other person, under Chapters II, LVII, LVIII and LIX.')
heading_25 = heading_22 + ">>(3)"
CONTEXT(heading_25, 'An officer of a body corporate may be charged with, and convicted of, an offence in accordance with section 39, 40 or 41 whether or not the body corporate is charged with, or convicted of, the offence committed by the body corporate.')
heading_26 = heading_22 + ">>(4) If an officer of a body corporate who is charged with an offence in accordance with section 39, 40 or 41 claims that the body"
heading_27 = heading_26 + ">>a"
CONTEXT(heading_27, 'the onus of proving the defence is on the officer and')
heading_28 = heading_26 + ">>b"
CONTEXT(heading_28, 'the standard of proof required is the standard that would apply to the body corporate in relation to the defence.')
heading_27 = heading_22 + ">>(5) Subsection (4) does not limit any other defence available to the officer."
CONTEXT(heading_27, '[Section 42 inserted No. 9 of 2023 s. 5.]')
heading_23 = heading_15 + ">>43. Penalties"
heading_24 = heading_23 + ">>(1) This section applies to an offence if "
heading_25 = heading_24 + ">>a"
CONTEXT(heading_25, 'section 39, 40 or 41 applies to the offence and')
heading_26 = heading_24 + ">>b"
CONTEXT(heading_26, 'the specified penalty for the offence applies only to a body corporate.')
heading_25 = heading_23 + ">>(2)"
