CONTEXT(heading_33, '(ii) a person registered under the  Health Practitioner Regulation National Law (Western Australia) in the psychology profession')
heading_34 = heading_32 + ">>content2"
CONTEXT(heading_34, '(iii) a social worker as defined in the  Mental Health Act 2014 section 4')
heading_35 = heading_32 + ">>(iv) a police officer"
heading_36 = heading_35 + ">>v"
CONTEXT(heading_36, 'a person in charge of a womens refuge')
